// MininAPI additionalFunctions
// Examples are using if you import into a var called 'af'

// Remove Array Item - Credit to qwertymk (http://stackoverflow.com/users/465546/qwertymk)
// Ex. af.removeArrayObject(array1, "item6");
exports.removeArrayObject = function(arr, what) {
    var found = arr.indexOf(what);

    while (found !== -1) {
        arr.splice(found, 1);
        found = arr.indexOf(what);
    }
    console.log("REMOVED OBJECT " + what + " FROM ARRAY");
}
// Random Number Generator
// Ex. var myInt = af.random(1, 6);
exports.randomInt = function(minimum, maximum) {
    var random = Math.floor(Math.random() * maximum) + minimum;
    return random;
}
// Random String Generator - Modified, original by csharptest.net (http://stackoverflow.com/users/164392/csharptest-net)
// Ex. var myString = af.randomString(12);
exports.randomString = function(length) {
    var text = "";
    var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz";

    for( var i=0; i < length; i++ )
        text += possible.charAt(Math.floor(Math.random() * possible.length) + 0);

    return text;
}
// HTML Log
// Ex. af.log('Hello World!', 'pageContent');
exports.log = function(content, id) {
    let node = document.createElement("div");
    let textnode = document.createTextNode(content);
    node.appendChild(textnode);
    document.getElementById(id).appendChild(node);
}

// Format Seconds - Modified, original by XerXis (on NeoWin.net)
// Ex. af.formatSecs(3064);
exports.formatSecs = function(seconds) {
    // Get Days/Hours/Minutes/Seconds
    var numdays = Math.floor(seconds / 86400);
    var numhours = Math.floor((seconds % 86400) / 3600);
    var numminutes = Math.floor(((seconds % 86400) % 3600) / 60);
    var numseconds = ((seconds % 86400) % 3600) % 60;

    // Format Calculations
    var result = numdays + ":" + numhours + ":" + numminutes + ":" + numseconds;

    // Return Result
    return result;
}